// /// 9. Asynchronous

// contoh konsep synchronous
function sayHello() {
  return `Hello!`;
}
// berurutan
console.log('Synchronous');
console.log('mulai');
console.log(sayHello());
console.log('selesai');

// contoh asynchronous
console.log('Asynchronous');
console.log('mulai');
setTimeout(() => console.log('Hello 2'), 3000); // akan dilewati dan muncul setelah 3 detik
console.log('selesai');

